import React, { useEffect, useState } from "react"

const Landing = () => {
    // const [titleData, setTitleData] = useState("")
    // const [postData, setPostData] = useState([])
    const [loading, setLoading] = useState(true)

    const [responseAll, setResponseAll] = useState({posts: null, title: null, subheading: null})

    useEffect(() => {
        const getData = async () => {
            // promise.all 
            const [respPosts, respTitle, respSubheading] = await Promise.all([
                fetch("http://localhost:1337/api/posts"), 
                fetch("http://localhost:1337/api/main-title"), 
                fetch("http://localhost:1337/api/subheading")
            ])
            setResponseAll({
                posts: await respPosts.json(), 
                title: await respTitle.json(), 
                subheading: await respSubheading.json()
            })
            setLoading(false)


        }
        getData()
    }, [])

    return (
        <>
            {
                loading ? 
                <p>Content is loading...</p> 
                :
                <div>
                    <h1>{responseAll.title.data.attributes.title}</h1> 
                    <h1>{responseAll.subheading.data.id}</h1> 
                    {
                        responseAll.posts.data.map((item) => {
                            return (
                                <div key={item.id}>
                                    <h2>{item.attributes.Title}</h2>
                                    <h2>{item.attributes.Body}</h2>
                                </div>
                            )
                        })   
                    }          
                </div>
            }
        </>
    )
}

export default Landing
