import React, { useEffect, useState } from "react"

const Landing = () => {
    const [responseAll, setResponseAll] = useState({title: null, subheading: null, intro: null, images: null})
    const [loading, setLoading] = useState(true)

    useEffect(() => {
        const getData = async () => {
            const [responseTitle, responseSubheading, responseIntro, responseImages] = await Promise.all([
                fetch("http://localhost:1337/api/main-title"), 
                fetch("http://localhost:1337/api/subheading"),
                fetch("http://localhost:1337/api/intro-text"),
                fetch("http://localhost:1337/api/upload/files"),
            ])
            setResponseAll({
                title: await responseTitle.json(), 
                subheading: await responseSubheading.json(),
                intro: await responseIntro.json(),
                images: await responseImages
            })
            setLoading(false)
        }
        getData()
    }, [])

    console.log('images', responseAll)

    return (
        <>
            {
                loading ? 
                <p>Content is loading...</p> 
                :
                <>
                    <div>
                        <h1>{responseAll.title.data.attributes.title}</h1> 
                        <h1>{responseAll.subheading.data.attributes.subheading}</h1>       
                    </div>
                    <div>
                        <h3>{responseAll.intro.data.attributes.introduction}</h3>
                    </div>
                </>
            }
        </>
    )
}

export default Landing
