import React, { useEffect, useState } from "react"

const Landing = () => {
    const [titleData, setTitleData] = useState(null)
    const [postData, setPostData] = useState([])
    const [loading, setLoading] = useState(true)

    useEffect(() => {
        const getPosts = async () => {
            let response = await fetch("http://localhost:1337/api/posts")
            let data = await response.json()
            setPostData(data)
            setLoading(false)
        }
        const getTitle = async () => {
            let response = await fetch("http://localhost:1337/api/main-title")
            let data = await response.json()
            setTitleData(data)
        }
        getPosts()
        getTitle()
    }, [])

    console.log('this is the title data state:')
    console.log(titleData)
    // console.log(titleData.data.attributes.title)

    return (
        <>
            {/* {
                titleData ?
                    <h1>{titleData}</h1>
                    : 
                    <h1>Title loading</h1>
            } */}
            {
                loading ? 
                <p>Content is loading...</p> 
                    :     
                    postData.data.map((item) => {
                        return (
                            <div>
                                <h2>{item.attributes.Title}</h2>
                                <h2>{item.attributes.Body}</h2>
                            </div>
                        )
                    })       
            }
        </>
    )
}

export default Landing
