import React, { useEffect, useState } from "react"

const Landing = () => {
    const [titleData, setTitleData] = useState("")
    const [postData, setPostData] = useState([])
    const [loading, setLoading] = useState(true)

    useEffect(() => {
        const getData = async () => {
            let response = await fetch("http://localhost:1337/api/posts")
            let data = await response.json()
            setPostData(data)

            let titleRes = await fetch("http://localhost:1337/api/main-title")
            let titleTextData = await titleRes.json()
            setTitleData(titleTextData)

            setLoading(false)
        }
        getData()
    }, [])

    console.log('this is the title data state:')
    console.log(titleData)
    // console.log(titleData.data.attributes.title)

    return (
        <>
            {
                loading ? 
                <p>Content is loading...</p> 
                :
                `
                    <h1>{titleData.data.attributes.title}</h1>     
                    postData.data.map((item) => {
                        return (
                            <div>
                                <h2>{item.attributes.Title}</h2>
                                <h2>{item.attributes.Body}</h2>
                            </div>
                        )
                    })       
                `
            }
        </>
    )
}

export default Landing
