import React, { useEffect, useState } from "react";
import landingImage from "../assets/woman-talking-unsplash.jpg";
import BounceLoader from "react-spinners/BounceLoader";
import styles from "../styles/ImageMain.module.css"

const ImageMain = () => {
    // const [mainImage, setMainImage] = useState(null)
    const [loading, setLoading] = useState(true)

    useEffect(() => {
        // const getImage = async () => {
        //     let response = await fetch("http://localhost:1337/api/upload/files")
        //     let imageData = await response.json()
        //     console.log(imageData)
        //     setMainImage(imageData)
        //     setLoading(false)
        // }
        // getImage()
        setTimeout(() => {
            setLoading(false)
        }, 2000);
    }, [])

    return (
        <>
            {
                loading ? 
                    <BounceLoader
                        // color={red}
                        loading={loading}
                        // cssOverride={override}
                        size={150}
                        aria-label="Loading spinner"
                        data-testid="loader"
                    />        
                : 
                <>
                    <div className={styles.imageContainer}>
                        <img className={styles.mainImage} src={landingImage} alt="Main landing image"/>
                    </div>
                </>
            }
        </>
    )
}

export default ImageMain
